import React, { useState } from 'react'
import { Form, Button } from 'react-bootstrap'
import { useEffect } from 'react';
import axios from 'axios';

const Opret = ({ addJoke }) => {

  const [title, setTitle] = useState('');
  const [jokeText, setjokeText] = useState('');

  useEffect(() => {
    axios.post('http://localhost:3000/jokes')
      .then(res => {
        console.log(res);
        addJoke(res.data)

      })
  }, [])


  const handleSubmit = (e) => {
    e.preventDefault();
    addJoke({ title, jokeText });
    setTitle();
    setjokeText();
  }


  return (
    <div className="container">

      <h1 className="text-center mb-5">Opret en ny joke</h1>

      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formBasicEmail">

          <Form.Control type="text" value={title} required onChange={(e) => setTitle(e.target.value)} placeholder="Joke overskrift" />

          <Form.Text className="text-muted">

          </Form.Text>
        </Form.Group>

        <div class="form-group">

          <textarea type="text" value={jokeText} required onChange={(e) => setjokeText(e.target.value)} class="form-control" id="exampleFormControlTextarea1" placeholder="JokeTekst her..." rows="3"></textarea>
        </div>

        <input variant="primary" type="submit">
          Gem Joke
  </input>


      </Form>


    </div>
  )
}

export default Opret;
